import { useEffect, useState } from "react";

function App() {
  const [employees, setEmployees] = useState([]);
  const [teams, setTeams] = useState([]);
  const [roles, setRoles] = useState([]);
  const [form, setForm] = useState({ name: "", work_method: "", team_id: "" });

  useEffect(() => {
    loadEmployees();
    fetch("http://localhost:4000/teams").then(r => r.json()).then(setTeams);
    fetch("http://localhost:4000/roles").then(r => r.json()).then(setRoles);
  }, []);

  const loadEmployees = () => {
    fetch("http://localhost:4000/employees")
      .then(r => r.json())
      .then(setEmployees)
      .catch(err => console.error("Error cargando empleados:", err));
  };

  const addEmployee = (e) => {
    e.preventDefault();
    fetch("http://localhost:4000/employees", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    })
      .then(r => r.json())
      .then(() => {
        loadEmployees();
        setForm({ name: "", work_method: "", team_id: "" });
      });
  };

  return (
    <div className="p-4">
      <h1>GestiÃ³n de Empleados ðŸš€</h1>

      <form onSubmit={addEmployee}>
        <input
          placeholder="Nombre"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <select
          value={form.work_method}
          onChange={(e) => setForm({ ...form, work_method: e.target.value })}
        >
          <option value="">MÃ©todo de trabajo</option>
          <option value="Remoto">Remoto</option>
          <option value="HÃ­brido">HÃ­brido</option>
          <option value="Presencial">Presencial</option>
        </select>
        <select
          value={form.team_id}
          onChange={(e) => setForm({ ...form, team_id: e.target.value })}
        >
          <option value="">Seleccione equipo</option>
          {teams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
        </select>
        <button type="submit">Agregar</button>
      </form>

      <h2>Empleados</h2>
      <table>
        <thead>
          <tr>
            <th>Nombre</th><th>Email</th><th>MÃ©todo</th><th>Equipo</th><th>LÃ­der</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(e => (
            <tr key={e.id}>
              <td>{e.name}</td>
              <td>{e.email}</td>
              <td>{e.work_method}</td>
              <td>{e.team}</td>
              <td>{e.leader}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
