import { useEffect, useState } from "react";

export default function App() {
  const [employees, setEmployees] = useState([]);
  const [teams, setTeams] = useState([]);
  const [roles, setRoles] = useState([]); // por si luego lo muestras
  const [credentials, setCredentials] = useState([]);

  const [form, setForm] = useState({
    name: "",
    work_method: "",
    team_id: ""
  });

  const loadAll = () => {
    fetch("http://localhost:4000/employees").then(r=>r.json()).then(setEmployees).catch(console.error);
    fetch("http://localhost:4000/teams").then(r=>r.json()).then(setTeams).catch(console.error);
    fetch("http://localhost:4000/roles").then(r=>r.json()).then(setRoles).catch(console.error);
    fetch("http://localhost:4000/credentials").then(r=>r.json()).then(setCredentials).catch(console.error);
  };

  useEffect(() => { loadAll(); }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = {
      ...form,
      team_id: form.team_id ? Number(form.team_id) : null
    };
    fetch("http://localhost:4000/employees", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    })
      .then(r => r.json())
      .then(data => {
        if (data.credentials) {
          alert(`âœ… Credenciales creadas:\nUsuario: ${data.credentials.username}\nPassword: ${data.credentials.password}`);
        }
        setForm({ name: "", work_method: "", team_id: "" });
        loadAll();
      })
      .catch(err => console.error("Error creando empleado:", err));
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h1>GestiÃ³n de Empleados ðŸš€</h1>

      <form onSubmit={handleSubmit} style={{ display: "grid", gap: 8, maxWidth: 720, marginBottom: 24 }}>
        <input
          name="name"
          placeholder="Nombre completo"
          value={form.name}
          onChange={handleChange}
          required
        />

        <div style={{ display: "flex", gap: 8 }}>
          <select name="work_method" value={form.work_method} onChange={handleChange} required>
            <option value="">MÃ©todo de trabajo</option>
            <option value="Remoto">Remoto</option>
            <option value="HÃ­brido">HÃ­brido</option>
            <option value="Presencial">Presencial</option>
          </select>

          <select name="team_id" value={form.team_id} onChange={handleChange}>
            <option value="">Equipo (opcional)</option>
            {teams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </div>

        <button type="submit">Agregar empleado</button>
      </form>

      <h2>ðŸ“‹ Empleados</h2>
      <table width="100%" border="1" style={{ borderCollapse: "collapse", marginBottom: 24 }}>
        <thead>
          <tr>
            <th>#</th><th>Nombre</th><th>Email corporativo</th><th>MÃ©todo</th><th>Equipo</th><th>LÃ­der</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(e => (
            <tr key={e.id}>
              <td>{e.id}</td>
              <td>{e.name}</td>
              <td>{e.email}</td>
              <td>{e.work_method}</td>
              <td>{e.team || "-"}</td>
              <td>{e.leader || "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>ðŸ‘¥ Equipos</h2>
      <table width="100%" border="1" style={{ borderCollapse: "collapse", marginBottom: 24 }}>
        <thead>
          <tr><th>#</th><th>Equipo</th><th>LÃ­der</th></tr>
        </thead>
        <tbody>
          {teams.map(t => (
            <tr key={t.id}>
              <td>{t.id}</td><td>{t.name}</td><td>{t.leader}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>ðŸ”‘ Credenciales</h2>
      <table width="100%" border="1" style={{ borderCollapse: "collapse" }}>
        <thead>
          <tr><th>#</th><th>Empleado</th><th>Usuario</th><th>Password</th></tr>
        </thead>
        <tbody>
          {credentials.map(c => (
            <tr key={c.id}>
              <td>{c.id}</td>
              <td>{c.name}</td>
              <td>{c.username}</td>
              <td>{c.password}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
